target.echo:
# User Defined Suppressions function suppressUserDefined() { if [ ! -f ./mvn-suppressions.sh ]; then cat < /dev/stdin elif [ -x ./mvn-suppressions.sh ]; then echo "[ERROR] The file \"mvn-suppressions.sh\" exists, but is not executable. You can make it executable using: \"git update-index --chmod=+x mvn-suppressions.sh\"" >&2 false else cat < /dev/stdin \ | ./mvn-suppressions.sh fi }
# PMD: The Maven Plugin tries to perform Java type resolution on POM typed projects. function suppressPmdWarnings() { cat < /dev/stdin \ | grep --invert-match --perl-regexp "^\\[WARNING\\] The project parent does not seem to be compiled\\. PMD results might be inaccurate\\.$" \ | grep --invert-match --perl-regexp "^\\[WARNING\\] Auxclasspath entry .*[/\\\\]parent[/\\\\]target[/\\\\]classes doesn't exist, ignoring it$" }
# JDK8: Checksums of github repositories cannot be validated successfully from time to time. function suppressJdk8Warnings() { if [ "${JDK_VERSION}" -eq 8 ]; then cat < /dev/stdin \ | grep --invert-match --perl-regexp "^\\[WARNING\\] Checksum validation failed, expected [a-z0-9]+ but is [a-z0-9]+ from github for https://maven.pkg.github.com/.+/maven-metadata.xml$" \ | grep --invert-match --perl-regexp "^\\[WARNING\\] Could not validate integrity of download from https://maven.pkg.github.com/.+/maven-metadata.xml: Checksum validation failed, expected [a-z0-9]+ but is [a-z0-9]+$" else cat < /dev/stdin fi }
JDK_VERSION=`echo "$1" | grep --only-matching --perl-regexp "^(1\\\\.)?\\\\K\\\\d+$"` if [ -z "${JDK_VERSION}" ]; then echo "[ERROR] The given JDK version is empty. Given value: \"$1\"" >&2 false else cat < /dev/stdin \ | suppressUserDefined \ | suppressPmdWarnings \ | suppressJdk8Warnings fi

# ${project.name}
${project.description}

